	<script>			

		function addPerson(over18) {
			var peopleNr = jQuery('.householdMember').length + 1;
			var newPerson =  '<div id="people_record['+peopleNr+']" class="householdMember horizontalInput">';

			if(over18) {
				newPerson += '<label class="requiredField" for="people[' + peopleNr + '][mobile_phone]">Mobiltlf</label>';
				newPerson += '<input type="text" name="people[' + peopleNr + '][mobile_phone]" id="people[' + peopleNr + '][mobile_phone]" class="w3 validate[required,custom[noWhiteSpace]],custom[phone]]" maxlength="8" value="" onkeyup="AddressChange.doMobilePhoneLookUp(this.value,\'people['+ peopleNr +']\')" />';
			}
				newPerson += '<label class="requiredField" for="people['+peopleNr+'][firstname]">Fornavn</label>';
				newPerson += '<input type="text" name="people['+peopleNr+'][firstname]" id="people['+peopleNr+'][firstname]" class="w6 validate[required]" value=""/>';
				
				newPerson += '<label class="requiredField" for="people['+peopleNr+'][lastname]">Etternavn</label>';
				newPerson += '<input type="text" name="people['+peopleNr+'][lastname]" id="people['+peopleNr+'][lastname]" class="w6 validate[required]" value=""/>';
				
			if(over18) {
				newPerson += '<input type="hidden" name="people['+peopleNr+'][email]" id="people['+peopleNr+'][email]" value=""/>';
			} else {
				newPerson += '<input type="hidden" name="people[' + peopleNr + '][email]" id="people[' + peopleNr + '][email]" value="-1"/>';
			}

			newPerson += '<input type="hidden" name="people['+peopleNr+'][birth_date]" id="people['+peopleNr+'][birth_date]" value=""/>';

			newPerson += '<span class="floatRight"><a href="javascript:void(0)" onclick="removePerson('+ peopleNr +')"><span class="icon-minus-3"></span> Fjern personen over</a></span>';
			newPerson += '</div>';

			if(peopleNr<6) { //Max 6 member allowd per household
				jQuery('#householdMembers').append(newPerson);
				
				if(peopleNr==5) { //Last person
					jQuery('.addPerson').first().before('<span>Det er ikke mulig å legge inn flere enn 6 personer per adresseendring</span>');
				}
			}
			jQuery('#addressForm').validationEngine('attach', {scroll: false});
		}

		function removePerson(id)
		{
			id = "people_record["+ id +"]";

			var list = jQuery(".householdMember");
			var isFound = false;
			var i = 0;
			while (!isFound && i <= list.length)
			{
				if (list[i].id == id)
				{
					list[i].remove();
					isFound = true;
				}

				i++;
			}
		}
	
	</script>
	

